<?phpdefined('BASEPATH') OR exit('No direct script access allowed');class setting extends CI_Controller {	public function __construct(){		parent::__construct();		session_start();	}	public function loadpage($StrQuery){		//Data		$data['Result'] = $StrQuery['Result'];		$data['namepage'] = $StrQuery['namepage'];		// View		$this->load->view('/Template/Header', $data);		$this->load->view($StrQuery['View']);		$this->load->view('/Template/Footer');	}		//Webpage	public function SetMaxWork(){		$StrQuery = array(			'Result' => $this->officermodel->max_work_list(),			'namepage' =>'ข้อมูลนักศึกษา' ,			'View' => 'Graduate/Pages/SetMaxWork'		);				$this->loadpage($StrQuery);	}	//Process	public function SaveMaxWork(){		$input = array(		'MAX_WORK_ID' => $_POST['MAX_WORK_ID'],		'MAX_WORK_YEAR' => $_POST['MAX_WORK_YEAR'],		'MAX_WORK_TS' => $_POST['MAX_WORK_TS'], // Thesis		'MAX_WORK_IS' => $_POST['MAX_WORK_IS'], // Independent Study		);		$this->officermodel->max_work_list();		redirect($this->agent->referrer());	}	public function DelMaxWork(){		$input = $this->uri->segment(3);		$this->officermodel->max_work_list();		redirect($this->agent->referrer());	}	}