onthCut[$strMonth];    return "$strDay $strMonthThai $strYear เวลา $strHour:$strMinute น.";  }  public function FullDateThai($strDate)  {    // 2008-08-14 13:42:44    $strYear = date("Y",strtotime($strDate))+543;    $strMonth= date("n",strtotime($strDate));    $strDay= date("j",strtotime($strDate));    $strHour= date("H",strtotime($strDate));    $strMinute= date("i",strtotime($strDate));    $strSeconds= date("s",strtotime($strDate));    $strMonthCut = Array("","มกราคม","กุมภาพันธ์","มีนาคม","เมษายน","พฤษภาคม","มิถุนายน","กรกฎาคม","สิงหาคม","กันยายน","ตุลาคม","พฤศจิกายน","ธันวาคม");    $strMonthThai=$strMonthCut[$strMonth];    return "วันที่ $strDay $strMonthThai $strYear เวลา $strHour:$strMinute น.";  }  public function inputDateThai($strDate, $inputName)  {    $thDate = $this->DateThai($strDate);    echo "<p>".$thDate."</p>";    echo "<input class='form-control' type='hidden' name='".$inputName."' value='".$strDate."' >";  }  public function inputFullDateThai($strDate, $inputName)  {    $thDate = $this->FullDateThai($strDate);    echo "<p>".$thDate."</p>";    echo "<input class='form-control' type='datetime' name='".$inputName."' value='".$strDate."' >";  }  public function diff_date_edit($date){	$startTimeStamp = new DateTime($date);//+60	$endTimeStamp = $startTimeStamp->modify('+60 day');	$nowTimeStamp = new DateTime('now');	$timeDiff = date_diff($nowTimeStamp, $endTimeStamp);	$numberDays = $timeDiff->format('%a');	$numberDays2 = $timeDiff->format('%a');	if($numberDays<0)	{		echo "<script>$('.btn-edit').hide();</script>";	}	elseif ($numberDays=0)	{		echo "วันสุดท้าย";	}	else	{		echo $numberDays2." วัน";	}  }} /* End of file Someclass.php */