<?phpdefined('BASEPATH') OR exit('No direct script access allowed');class docsmodel extends CI_Model {  public function adviser($input)  {	$this->db->where('APPOINT_AVISER_IS_THESIS_ID', $input['APPOINT_AVISER_IS_THESIS_ID']);	$this->db->update('doc_adviser', $input);  }  public function proposal($input)  {	$this->db->where('EXAM_PROPOSAL_THESIS_IS_ID', $input['EXAM_PROPOSAL_THESIS_IS_ID']);	$this->db->update('doc_exam_proposal', $input);  }  public function qece($input)  {	$this->db->where('COMPREHENSIVEID', $input['COMPREHENSIVEID']);	$this->db->update('doc_exam_qece', $input);  }  public function thesis($input)  {	$this->db->where('QUALIFYINGID', $input['QUALIFYINGID']);	$this->db->update('doc_exam_thesis', $input);  }}