<?phpdefined('BASEPATH') OR exit('No direct script access allowed');class officermodel extends CI_Model {  public function officer_relate()  {    $this->db->join('attribute_prefix', 'officer.PREFIXID = attribute_prefix.PREFIXID','LEFT');    $this->db->join('attribute_faculty', 'officer.FACULTYID = attribute_faculty.FACULTYID','LEFT');    $this->db->join('attribute_department', 'officer.DEPARTMENTID = attribute_department.DEPARTMENTID','LEFT');  }  public function officersall()  {    $this->officer_relate();    $query = $this->db->get('officer');    return $query->result_array();  }  public function OfficerInfo($input)  {    $this->db->where('officer.OFFICERCODE', $input);    $this->officer_relate();    $query = $this->db->get('officer');    return $query->result_array();  }  public function OfficerDetail($input)  {    $this->db->where('officer.OFFICERID', $input);    $this->officer_relate();    $query = $this->db->get('officer');    return $query->result_array();  }  public function WorkByOfficer($input)  {    //WHERE Table1.Culumn , Value    // $this->db->where('relate_classroom_officer.OFFICERID', $input);    //    // //Join inner    // //Table2 , Table1.FK = Table2.PK    // $this->db->join('classroom', 'relate_classroom_officer.RMU_CLASSID = classroom.RMU_CLASSID');    // $this->db->join('officer', 'relate_classroom_officer.OFFICERID = officer.OFFICERID');    //Join Another Table    //Table3 , Table2.FK = Table3.PK    // $this->db->join('course', 'classroom.COURSEID = course.COURSEID');    // Select Table1    $query = $this->db->get('relate_classroom_officer');    return $query->result_array();  }  public function AuthenOfficer($input)  {    $this->db->where('USERCODE',$input['userName']);    $this->db->where('USERPASS',$input['password']);    $query = $this->db->get('user_group');    return $query->result_array();  }  public function SaveOfficerPic($input)  {    $this->db->where('OFFICERID', $input['OFFICERID']);    $this->db->update('officer', $input);  }  public function officersByProg($input)  {    $this->db->where('PROGRAMID', $input);    $this->db->join('officer', 'relate_program_officer.OFFICERID = officer.OFFICERID');    $this->db->order_by("PROG_OFFICER_POSITION", "ASC");    $query = $this->db->get('relate_program_officer');    return $query->result_array();  }  public function max_work_list()  {  }  public function getOfficerInfo($arr){    $this->db->like($arr);    $query = $this->db->get('officer');    return $query->result_array();  }  public function getDepartmentInfoById($id){    $arr = array('DEPARTMENTID' => $id, );    $query = $this->db->get_where('attribute_department',$arr);    $department = $query->result_array();    echo $department[0]['DEPARTMENTNAME'];  }}