<button class="btn btn-primary btn-lg" data-toggle="modal" data-target="#myModal">Launch demo modal</button><div class="modal fade" id="myModal" tabindex="-1" role="dialog" aria-labelledby="myModalLabel" aria-hidden="true">    <div class="modal-dialog">    <div class="modal-content">        <div class="modal-header">            <button type="button" class="close" data-dismiss="modal" aria-hidden="true">Ã—</button>            <h4 class="modal-title" id="myModalLabel">Modal title</h4>        </div>        <div class="modal-body">            <p>Modal dialog content...</p>        </div>        <div class="modal-footer">            <button type="button" class="btn btn-default" data-dismiss="modal"><i class="fa fa-times-circle"></i> Close</button>            <button type="button" class="btn btn-custom-primary"><i class="fa fa-check-circle"></i> Save changes</button>        </div>    </div>    </div></div>