<div class="row">  <div class="col-md-12">    <div class="dataTables_wrapper" id="example_wrapper">      <div class="widget widget-table">        <div class="widget-header">          <h3><i class="fa fa-table"></i>รายการหลักสูตร</h3>          <div class="btn-group widget-header-toolbar">            <button class="btn btn-success btn-sm" type="button" onClick="xlxsExport();">Export Excel</button>          </div>        </div>        <div class="widget-content">          <table id="example" class="table table-responsive table-sorting table-striped table-hover datatable dataTable no-footer" role="grid" aria-describedby="datatable-column-filter_info">            <thead>              <tr role="row">                <th>ชื่อหลักสูตร</th>                <th>คณะ</th>                <th>สาขาวิชา</th>                <th>ระดับปริญญา</th>                <th>ปีการศึกษา</th>                <th>นักศึกษา</th>                <th>เพิ่มเติม</th>              </tr>            </thead>            <tbody>              <?php foreach ($Result as $Result): ?>              <tr role="row" class="odd">                <td><?php echo $Result['PROGRAMNAME']; ?></td>                <td><?php echo $Result['FACULTYNAME']; ?></td>                <td><?php echo $Result['DEPARTMENTNAME']; ?></td>                <td><?php echo $Result['LEVELNAME']; ?></td>                <td style="width:10%"><?php echo $Result['PROGRAMYEAR']; ?></td>                <th style="width:10%"><?php echo $Result['AMT_STD']; ?> คน</th>                              </tr>              <?php endforeach; ?>            </tbody>          </table>        </div>      </div>    </div>  </div></div><script>function xlxsExport(){  $("#example").table2excel({    // exclude CSS class    exclude: ".noExl",	name: "Excel Document Name",	filename: "รายงานหลักสูตร",	fileext: ".xls",	exclude_img: true,	exclude_links: false,	exclude_inputs: false  }); }</script>